import { createSlice } from "@reduxjs/toolkit";

const addToCart =createSlice({
    name:'cart',
    initialState:{
        items:[],
        totalQuantity:0,
    },
    reducers: {
        addItemToCart(state,action){
            const newItem= action.payload;
            const existingItem = state.items.find((item)=>item.itemId === newItem.id)
            if(!existingItem){
               state. items.push({
                    itemId:newItem.id,
                    price:newItem.price,
                    qunatity:1,
                    totalPrice:newItem.price,
                 
                    name:newItem.title
                })
            }else{
                existingItem.qunatity= existingItem.qunatity+1;
                existingItem.totalPrice=newItem.price+existingItem.totalPrice
            }
        },
        removeItemFromCart(state,action){
            const id = action.payload;
            const existingItem= state.items.find((item)=>item.id === id)
            if(existingItem===1){
                state.items= state.items.filter((item)=>item.id!==id)

            }
            else{
                existingItem.qunatity--;
                // existingItem.totalPrice= existingItem.totalPrice - existingItem.price
            }

        }

    }
})

export const addToCartActions = addToCart.actions;


export default addToCart.reducer
